import React, { Component } from 'react';
import './Todopage.css';

class Todopage extends Component {
  state = {
    input: "",
    items: []
  };

  addvalues = (event) => {
    this.setState({
      input: event.target.value
    });
  };

  storeItems = (event) => {
    event.preventDefault();
    const { input } = this.state;
    if (input.trim() !== '') { // Check if input is not empty
      this.setState(prevState => ({
        items: [...prevState.items, input],
        input: "" // Clear input field after adding item
      }));
    }
  };

  render() {
    const { input, items } = this.state;

    return (
      <div className='Todopage'>
        <div className='mainbody'>
          <h2>Todo Content</h2>
          <div className='formbox'>
            <form className='input-form' onSubmit={this.storeItems}>
              <input type='text' name="inp1" onChange={this.addvalues} value={input} placeholder="Enter your todo item" />
            </form>
          </div>
          <div className='titlediv'>
            <ul>
              {items.map((item, index) => (
                <li key={index}>
                  {item}
                  <i className="fa-regular fa-trash-can"></i> {/* Placeholder for delete icon */}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Todopage;

